import React, { useEffect, useState } from "react";

import Comment from "./comment";
import fetchData from "../shared/sharedFunctions";
import { Routes } from "../config";
const { AllComments, Comments, ImageData } = Routes;

const CommentsComponent = props => {
  const [imageId, setImageId] = useState(imageID);
  const [PostData, setPostData] = useState("");
  const [comment, setComments] = useState("");

  useEffect(() => {
    allComments();
  });
  const { imageID } = props;
  const allComments = () => {
    const imageId = this.state.imageId;
    const data = {
      imageId: imageId
    };
    fetchData(AllComments, data)
      .then(res => {
        if (res) {
          const allCommentData = res.data.dataFromDatabase.map(data => {
            return data;
          });
          setComments(allCommentData);
        }
      })
      .catch(err => {
        if (err.message === "Network Error") {
          this.props.history.push("/errorpage");
        }
      });
  };

  const uploadComment = event => {
    event.preventDefault();
    let userid = localStorage?.getItem("userId");
    const commentData = {
      imageId: this.state?.imageId,
      comment: event.target?.comment?.value,
      userId: userid
    };
    fetchData(Comments, commentData)
      .then(res => {
        if (res) {
          let comments = res?.data?.dataFromDatabase?.map(data => {
            return data;
          });

          let imageID = { id: this.state?.imageId };
          fetchData(ImageData, imageID).then(res => {
            if (res && res.data) {
              setComments([...comments]);
              setPostData([res?.data?.dataBase[0]]);
              // this.setState({
              //   ,

              // });
            }
          });
        }
      })
      .catch(err => {
        if (err.message === "Network Error") {
          this.props.history.push("/errorpage");
        }
      });
    event.target.comment.value = null;
  };

  return (
    <ul>
      {comment.map((names, i) => (
        <Comment key={i} comments={names} />
      ))}
      <li>
        <div className="cmnt_div1">
          <form onSubmit={uploadComment}>
            <input
              type="text"
              placeholder="Enter your Comment"
              className="cmnt_bx1"
              name="comment"
              required
            />
            <input
              type="submit"
              className="sub_bttn1"
              defaultValue="Submit Comment"
            />
          </form>
        </div>
      </li>
    </ul>
  );
};
export default CommentsComponent;
