const CategoryList = props => {
  const allcategories = () => {
    fetchData(AllCategory)
      .then(res => {
        if (res && res.data) {
          let allCategoryData = res.data?.dataFromDatabase?.map(category => {
            return category;
          });
          this.props.updateCategory(allCategoryData);
        }
      })
      .catch(err => {
        this.setState({ hasError: true });
      });
  };

  useEffect(() => {
    allcategories();
  }, [props.category]);

  // componentDidMount() {
