const CommentsComponent = React.memo(props => {
  const { uploadComment, imageID, PostData } = props;
  const { noOfComments } = PostData;

  const [comment, setComment] = useState(props.comments);

  useEffect(() => {
    allComments();
  }, [setComment, noOfComments]);

  const allComments = () => {
    const data = {
