import React from "react";
import PropTypes from "prop-types";
import Helmet from "react-helmet";

import Categories from "../shared/categories";
import Post from "../timeline/posts";
import Featured from "../shared/featured";
import Comment from "./comment";
import PostActivityComponent from "../shared/postActivityComponent";
import CommentsComponent from "./commentsComponent";

export default function SinglePostComponent(props) {
  const {
    history,
    PostData,
    likePost,
    uploadComment,
    comment,
    Download,
    imageId
  } = props;
  return (
    <div>
      <Helmet>
        <title>Single Post</title>
      </Helmet>
      <link href="css/bootstrap.css" rel="stylesheet" type="text/css" />
      <link
        href="css/bootstrap-responsive.css"
        rel="stylesheet"
        type="text/css"
      />

      <div className="container">
        <div className="content">
          <div className="content_rgt">
            <Categories history={history} />
            <Featured />
          </div>
          <div className="content_lft">
            <ul>
              {PostData.map((names, i) => (
                <div>
                  <Post key={i} data={names} downLoad={Download} />
                </div>
              ))}
              {PostData ? (
                <PostActivityComponent
                  onlikePost={likePost}
                  Postdata={PostData[0]}
                  downLoad={Download}
                />
              ) : null}
            </ul>
            <div className="contnt_3">
              <CommentsComponent
                comments={comment}
                uploadComment={uploadComment}
                imageID={imageId}
                noOfComments={PostData[0]?.noOfComments}
              />
              {/* <ul>
                {comment.map((names, i) => (
                  <Comment key={i} comments={names} />
                ))}
                <li>
                  <div className="cmnt_div1">
                    <form onSubmit={uploadComment}>
                      <input
                        type="text"
                        placeholder="Enter your Comment"
                        className="cmnt_bx1"
                        name="comment"
                        required
                      />
                      <input
                        type="submit"
                        className="sub_bttn1"
                        defaultValue="Submit Comment"
                      />
                    </form>
                  </div>
                </li>
              </ul> */}
              <div className="view_div">
                <a href="#">View more</a>
              </div>
            </div>
          </div>
        </div>
        <div className="clear" />
      </div>
    </div>
  );
}
SinglePostComponent.propTypes = {
  history: PropTypes.object,
  PostData: PropTypes.array,
  likePost: PropTypes.func,
  uploadComment: PropTypes.func,
  comment: PropTypes.array,
  downLoad: PropTypes.func
};
