import React, { useState, useEffect } from "react";

import Comment from "./comment";
import fetchData from "../shared/sharedFunctions";
import { Routes } from "../config";

const { AllComments, Comments } = Routes;
const CommentsComponent = React.memo(props => {
  const { uploadComment, imageID, noOfComments, updatePostData } = props;

  const [comment, setComment] = useState(props.comments);

  useEffect(() => {
    allComments();
  }, [setComment, noOfComments]);

  const allComments = () => {
    const data = {
      imageId: imageID
    };
    fetchData(AllComments, data).then(res => {
      if (res && res.data) {
        const allCommentData = res.data.dataFromDatabase?.map(data => {
          return data;
        });
        setComment(allCommentData);
      }
    });
  };

  uploadComment = event => {
    event.preventDefault();
    let userid = localStorage?.getItem("userId");
    const commentData = {
      imageId: this.state?.imageId,
      comment: event.target?.comment?.value,
      userId: userid
    };
    fetchData(Comments, commentData)
      .then(res => {
        if (res && res.data) {
          updatePostData();
          // let postData = this.state.PostData;
          // postData[0].noOfComments = String(
          //   Number(postData[0].noOfComments) + 1
          // );
          // this.setState({ PostData: postData });
          // let imageID = { id: this.state?.imageId };
          // fetchData(ImageData, imageID).then(res => {
          //   if (res) {
          //     this.setState({
          //       PostData: [res?.data?.dataBase[0]],
          //       comment: [...comments]
          //     });
          //   }
          // });
        }
      })
      .catch(err => {
        if (err.message === "Network Error") {
          this.props.history.push("/errorpage");
        }
      });
    event.target.comment.value = null;
  };

  return (
    <ul>
      {comment
        ? comment.map((names, i) => <Comment key={i} comments={names} />)
        : null}
      <li>
        <div className="cmnt_div1">
          <form onSubmit={uploadComment}>
            <input
              type="text"
              placeholder="Enter your Comment"
              className="cmnt_bx1"
              name="comment"
              required
            />
            <input
              type="submit"
              className="sub_bttn1"
              defaultValue="Submit Comment"
            />
          </form>
        </div>
      </li>
    </ul>
  );
});
export default CommentsComponent;
