import React, { useCallback, useState } from "react";
// import InfiniteLoader from "react-window-infinite-loader";
import Helmet from "react-helmet";
// import { FixedSizeList } from "react-window";
import PropTypes from "prop-types";
import { connect } from "react-redux";

import Popup from "./popup.js";
import RightButton from "./rightButton";
import "./style.css";
import Featured from "../shared/featured";
import UserProfileComponent from "./userProfileComponent";
import CategoryList from "../shared/categories";
import Scroller from "./scroller";
import fetchData from "../shared/sharedFunctions";
import CategoryForm from "../shared/categoryForm";
import CategoryComponent from "../shared/CategoryComponent";
import { Routes } from "../config";

const { Upload_Category } = Routes;
// let clicked = false;

const TimelineBodyComponent = props => {
  // const getPost = ({ index, style }) => {
  //   if (props.postdata[index]) {
  //     return (
  //       <Post
  //         style={style}
  //         data={props.postdata[index]}
  //         likePost={props.likePost}
  //         downLoad={props.downLoad}
  //       />
  //     );
  //   } else {
  //     return null;
  //   }
  // };
  // const callBackFunction = useCallback(
  //   id => {
  //     props.likePost(id);
  //   },
  //   [props.likePost]
  // );

  const [clicked, setClicked] = useState(false);

  const categoryUploadForm = () => {
    if (!clicked) {
      setClicked(true);
      // const form =
      // "category<input type='text' name='category' required/><br /><br /><input type='submit'/> &emsp;";
      // document.getElementById("category").innerHTML = form;
      // } else {
      // document.getElementById("category").innerHTML = "";
      // clicked = false;
    }
  };

  const uploadCategory = function(event) {
    event.preventDefault();
    console.log("category", event.target.category.value);
    let check = false;
    props.category.map(data => {
      if (data.category === event.target.category.value.toLowerCase()) {
        check = true;
      }
    });
    if (check) {
      alert("This category already exist");
      event.target.category.value = "";
    } else {
      let newCategory = event.target.category.value;
      const categoryToBeUploaded = { category: newCategory };
      fetchData(Upload_Category, categoryToBeUploaded)
        .then(res => {
          if (res && res.data && res.data.status === "Category Inserted") {
            let allCategory = res?.data?.dataFromDatabase?.map(data => {
              return data;
            });
            this.props.updateCategory(allCategory);
          }
          // if (res && res.data?.status === "Already Exist") {
          // }
        })
        .catch(err => {
          if (err.message === "Network Error") {
            this.props.history.push("/errorpage");
          }
        });
      setClicked(false);
    }
  };

  const {
    togglePopup,
    // categoryUploadForm,
    // uploadCategory,
    username,
    showMyUploads,
    stateUpdateOnTimelineClick,
    handlePostUploadForm,
    // items,
    loadMorePosts,
    hasMoreItems,
    postdata,
    likePost,
    hasError,
    downLoad
  } = props;
  if (hasError) {
    return <div>Something is wrong</div>;
  }
  return (
    <div>
      <Helmet>
        <title>Timeline</title>
      </Helmet>
      <div className="container">
        <div className="content">
          <div className="content_rgt">
            <RightButton
              togglePopup={togglePopup.bind(this)}
              categoryUploadForm={categoryUploadForm}
            />
            <CategoryComponent
              uploadCategory={uploadCategory}
              clicked={clicked}
            />

            <Featured />
          </div>
          <div className="content_lft">
            <UserProfileComponent
              username={username}
              showMyUploads={showMyUploads}
              stateUpdateOnTimelineClick={stateUpdateOnTimelineClick}
            />
            <div>
              {props.showPopup ? (
                <Popup
                  handleSubmit={handlePostUploadForm}
                  togglePopup={togglePopup}
                />
              ) : null}
            </div>
            {/*<div> 
            <InfiniteLoader
                  isItemLoaded={() => {
                    return !hasMoreItems;
                  }}
                  style={{ width: "100" }}
                  itemCount={10}
                  loadMoreItems={() => {
                    loadMorePosts();
                  }}
                >
                  {({ onItemsRendered, ref }) => (
                    <FixedSizeList
                      height={3000}
                      width={750}
                      itemCount={10}
                      itemSize={650}
                      onItemsRendered={onItemsRendered}
                      ref={ref}
                    >
                      {getPost}
                    </FixedSizeList>
                  )}
                </InfiniteLoader> 
               </div>
               */}
            <Scroller
              onlikePost={likePost}
              postdata={postdata}
              downLoad={downLoad}
              loadMorePosts={loadMorePosts}
              hasMoreItems={hasMoreItems}
            />
          </div>
        </div>
      </div>
    </div>
  );
};
TimelineBodyComponent.propTypes = {
  logout: PropTypes.func,
  togglePopup: PropTypes.func,
  // categoryUploadForm: PropTypes.func,
  // uploadCategory: PropTypes.func,
  username: PropTypes.string,
  showMyUploads: PropTypes.func,
  stateUpdateOnTimelineClick: PropTypes.func,
  handlePostUploadForm: PropTypes.func,
  items: PropTypes.number,
  loadMorePosts: PropTypes.func,
  hasMoreItems: PropTypes.bool,
  postdata: PropTypes.array,
  likePost: PropTypes.func,
  showPopup: PropTypes.bool
};

function mapStateToProps(state) {
  return { category: state.CategoryReducer.category };
}

export default connect(mapStateToProps, null)(TimelineBodyComponent);
