import React, { useCallback, useState } from "react";
// import InfiniteLoader from "react-window-infinite-loader";
import Helmet from "react-helmet";
// import { FixedSizeList } from "react-window";
import PropTypes from "prop-types";

import Popup from "./popup.js";
import RightButton from "./rightButton";
import "./style.css";
import Featured from "../shared/featured";
import UserProfileComponent from "./userProfileComponent";
import Categories from "../shared/categories";
import Scroller from "./scroller";

let clicked = false;

export const TimelineBodyComponent = props => {
  // const getPost = ({ index, style }) => {
  //   if (props.postdata[index]) {
  //     return (
  //       <Post
  //         style={style}
  //         data={props.postdata[index]}
  //         likePost={props.likePost}
  //         downLoad={props.downLoad}
  //       />
  //     );
  //   } else {
  //     return null;
  //   }
  // };
  // const callBackFunction = useCallback(
  //   id => {
  //     props.likePost(id);
  //   },
  //   [props.likePost]
  // );

  const categoryUploadForm = () => {
    if (!clicked) {
      clicked = true;
      const form =
        "category<input type='text' name='category' required/><br /><br /><input type='submit'/> &emsp;";
      document.getElementById("category").innerHTML = form;
    } else {
      document.getElementById("category").innerHTML = "";
      clicked = false;
    }
  };

  const {
    togglePopup,
    // categoryUploadForm,
    uploadCategory,
    username,
    showMyUploads,
    stateUpdateOnTimelineClick,
    handlePostUploadForm,
    items,
    loadMorePosts,
    hasMoreItems,
    postdata,
    likePost,
    hasError,
    downLoad
  } = props;
  if (hasError) {
    return <div>Something is wrong</div>;
  }
  return (
    <div>
      <Helmet>
        <title>Timeline</title>
      </Helmet>
      <div className="container">
        <div className="content">
          <div className="content_rgt">
            <RightButton
              togglePopup={togglePopup.bind(this)}
              categoryUploadForm={categoryUploadForm}
            />
            <form id="category" onSubmit={uploadCategory}></form>
            <Categories hasError={hasError} />
            <Featured />
          </div>
          <div className="content_lft">
            <UserProfileComponent
              username={username}
              showMyUploads={showMyUploads}
              stateUpdateOnTimelineClick={stateUpdateOnTimelineClick}
            />
            <div>
              {props.showPopup ? (
                <Popup
                  handleSubmit={handlePostUploadForm}
                  togglePopup={togglePopup}
                />
              ) : null}
            </div>
            {/* <InfiniteLoader
                isItemLoaded={() => {
                  return !hasMoreItems;
                }}
                style={{ width: "100" }}
                itemCount={10}
                loadMoreItems={() => {
                  loadMorePosts();
                }}
              >
                {({ onItemsRendered, ref }) => (
                  <FixedSizeList
                    height={3000}
                    width={750}
                    itemCount={10}
                    itemSize={650}
                    onItemsRendered={onItemsRendered}
                    ref={ref}
                  >
                    {getPost}
                  </FixedSizeList>
                )}
              </InfiniteLoader> */}
            <Scroller
              onlikePost={likePost}
              postdata={postdata}
              downLoad={downLoad}
              loadMorePosts={loadMorePosts}
              hasMoreItems={hasMoreItems}
            />
          </div>
        </div>
      </div>
    </div>
  );
};
TimelineBodyComponent.propTypes = {
  logout: PropTypes.func,
  togglePopup: PropTypes.func,
  // categoryUploadForm: PropTypes.func,
  uploadCategory: PropTypes.func,
  username: PropTypes.string,
  showMyUploads: PropTypes.func,
  stateUpdateOnTimelineClick: PropTypes.func,
  handlePostUploadForm: PropTypes.func,
  items: PropTypes.number,
  loadMorePosts: PropTypes.func,
  hasMoreItems: PropTypes.bool,
  postdata: PropTypes.array,
  likePost: PropTypes.func,
  showPopup: PropTypes.bool
};
